INSERT INTO `products` VALUES (7, 'Tesla Cybertruck', '<h2>Si&ecirc;u xe b&aacute;n tải điện &ldquo;lập dị&rdquo; Tesla Cybertruck &ndash; B&aacute;n tải đến từ tương lai</h2>\r\n', '<h3><strong>Tesla Cybertruck</strong>&nbsp;l&agrave; chiếc xe &ldquo;hot&rdquo; nhất tr&ecirc;n truyền th&ocirc;ng thế giới những ng&agrave;y vừa qua. Với m&agrave;n ra mắt theo phong c&aacute;ch kh&ocirc;ng thể bất ngờ hơn, Cybertruck khiến tất cả thế giới phải nhắc đến t&ecirc;n m&igrave;nh.&nbsp;</h3>\r\n', 0, 100, '1657262428_s22 ultra.jpg', 6821870000, 23, 3, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (8, 'Toyota Vios', '<h2>Với lợi thế bền bỉ, chi ph&iacute; &ldquo;nu&ocirc;i thấp&rdquo;, giữ gi&aacute; cực tốt, t&iacute;nh thanh khoản cao,&nbsp;<a href=\"https://xetot.com/tin-xe/danh-gia-xe-toyota-vios/\" rel=\"noopener\" target=\"_blank\" title=\"Đánh giá xe Toyota Vios 2022\">Toyota Vios 2022</a>&nbsp;lu&ocirc;n mang đến sự an t&acirc;m cho chủ nh&acirc;n. Đ&oacute; l&agrave; l&yacute; do v&igrave; sao Vios b&aacute;n rất chạy tại Việt Nam đến mức được đặt biệt danh &ldquo;mẫu xe quốc d&acirc;n&rdquo;.</h2>\r\n', '<h3>Kh&ocirc;ng thể phủ nhận Vios c&ograve;n c&oacute; những điểm hạn chế hơn đối thủ, đặc biệt về mặt tiện nghi. Tuy nhi&ecirc;n, model hiện h&agrave;nh đ&atilde; được cải tiến về kiểu d&aacute;ng, bổ sung trang bị, t&iacute;nh năng nhằm cải thiện trải nghiệm người d&ugrave;ng.</h3>\r\n', 1, 100, '1657436767_samsung-galaxy-z-flip-3-1-1.jpg', 3981790000, 25, 1, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (9, 'Honda City 2021', '<h2>Hiện tại, c&aacute;c đại l&yacute; v&agrave; showroom của Honda tại Việt Nam cũng đ&atilde; bắt đầu nhận đặt xe v&agrave; dự kiến giao xe trong th&aacute;ng 1/2021, c&aacute;c bạn c&oacute; thể đến đại l&yacute; để l&agrave;m thủ tục mua xe ngay từ b&acirc;y giờ.&nbsp;</h2>\r\n', '<p>Honda City 2021 đ&atilde; c&oacute; m&agrave;n ra mắt trực tuyến tại Việt Nam. Gi&aacute; xe kh&ocirc;ng qu&aacute; cao so với thế hệ tiền nhiệm, cụ thể như sau:&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Honda City 2021 bản 1.5G: 529 triệu đồng&nbsp;</li>\r\n	<li>Honda City 2021 bản 1.5L: 569 triệu đồng</li>\r\n	<li>Honda City 2021 bản 1.5RS: 599 triệu đồng</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với bản RS v&agrave; bản L nếu mua m&agrave;u đỏ người mua sẽ phải trả th&ecirc;m 5 triệu đồng v&agrave; m&agrave;u đỏ chỉ xuất hiện tr&ecirc;n 2 phi&ecirc;n bản cao cấp nhất của Honda City thế hệ 5.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 100, '1657443805_vn-top-loader-wa12t5360bv-wa12t5360bv-sv-514995811.jpg', 8443350000, 27, 22, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (10, 'KIA Carnival', '<h2>KIA Carnival l&agrave; mẫu MPV cỡ lớn th&agrave;nh c&ocirc;ng ở nhiều thị trường, trong đ&oacute; c&oacute; Việt Nam ngay từ khi c&ograve;n mang t&ecirc;n Sedona (Tham khảo:&nbsp;<a href=\"https://xetot.com/toan-quoc/mua-ban-oto/kia--sedona\" rel=\"noopener\" target=\"_blank\" title=\"Xe Kia Sedona cũ\">Xe Kia Sedona cũ</a>). Kh&ocirc;ng chỉ mang đến một kh&ocirc;ng gian rộng r&atilde;i, thế hệ mới của KIA Carnival ra mắt hơn 3 năm trước đ&atilde; được chắp b&uacute;p bởi nh&agrave; thiết kế từng nhiều năm l&agrave;m việc cho BMW. Nhờ đ&oacute;, KIA Carnival đ&atilde; c&oacute; bước đột ph&aacute; về diện mại.</h2>\r\n', '<h2><strong>Th&ocirc;ng số kỹ thuật</strong></h2>\r\n\r\n<ul>\r\n	<li><strong>Ph&acirc;n kh&uacute;c: MPV cỡ lớn</strong></li>\r\n	<li><strong>Số chỗ ngồi: 4 &ndash; 7 &ndash; 9</strong></li>\r\n	<li>Chiều D&agrave;i&nbsp; x Rộng x Cao (mm):&nbsp;5.155 x 1.995 x 1.775</li>\r\n	<li>Chiều d&agrave;i cơ sở (mm): 3.090</li>\r\n	<li>Khoảng s&aacute;ng gầm (mm): 172</li>\r\n	<li>B&aacute;n k&iacute;nh v&ograve;ng quay tối thiểu (m): 5,8</li>\r\n	<li>Động cơ:</li>\r\n	<li>Hybrid 1.6L tăng &aacute;p mới</li>\r\n	<li>Diesel 2.2L</li>\r\n	<li>Xăng 3.5L V6&nbsp;</li>\r\n	<li>C&ocirc;ng suất (m&atilde; lực):\r\n	<ul>\r\n		<li>242</li>\r\n		<li>199</li>\r\n		<li>268</li>\r\n	</ul>\r\n	</li>\r\n	<li>M&ocirc; men xoắn (Nm):\r\n	<ul>\r\n		<li>367</li>\r\n		<li>440</li>\r\n		<li>331</li>\r\n	</ul>\r\n	</li>\r\n	<li>Hệ dẫn động:</li>\r\n</ul>\r\n', 0, 100, '1657443637_vn-front-loading-washer-ww10t634dles3-ww90t634dle-sv-514976699.jpg', 1271380000, 37, 5, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (11, 'Honda City 2021', '<h2><strong>Cập nhật gi&aacute; xe Honda City 2021 v&agrave; th&ocirc;ng tin xe, th&ocirc;ng số kỹ thuật, h&igrave;nh ảnh, tin khuyến m&atilde;i, ưu đ&atilde;i k&egrave;m gi&aacute; lăn b&aacute;nh Honda City mới nhất th&aacute;ng 01/2024 tại Việt Nam.</strong></h2>\r\n', '<p>Honda City 2021 đ&atilde; c&oacute; m&agrave;n ra mắt trực tuyến tại Việt Nam. Gi&aacute; xe kh&ocirc;ng qu&aacute; cao so với thế hệ tiền nhiệm, cụ thể như sau:&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Honda City 2021 bản 1.5G: 529 triệu đồng&nbsp;</li>\r\n	<li>Honda City 2021 bản 1.5L: 569 triệu đồng</li>\r\n	<li>Honda City 2021 bản 1.5RS: 599 triệu đồng</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với bản RS v&agrave; bản L nếu mua m&agrave;u đỏ người mua sẽ phải trả th&ecirc;m 5 triệu đồng v&agrave; m&agrave;u đỏ chỉ xuất hiện tr&ecirc;n 2 phi&ecirc;n bản cao cấp nhất của Honda City thế hệ 5.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 100, '1657442833_vn-air-purifier-ax60r5080wdsv-ax60r5080wd-sv-frontwhite-189196503.jpg', 9026830000, 35, 14, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (12, ' Ford Escape', '<h2>Nhắc đến Ford Escape, nhiều kh&aacute;ch h&agrave;ng đ&atilde; kh&ocirc;ng khỏi tiếc nuối cho một huyền thoại đ&atilde; từng &ldquo;l&agrave;m mưa l&agrave;m gi&oacute;&rdquo; tại thị trường Việt Nam. Với khả năng vận h&agrave;nh mạnh mẽ đ&aacute;ng kinh ngạc, Ford Escape từng l&agrave; sự lựa chọn h&agrave;ng đầu v&agrave;o thời điểm 13 năm trước.</h2>\r\n', '<h2><strong>Năm 2001-2004 (thời kỳ ho&agrave;ng kim của Ford Escape tại Việt Nam)</strong></h2>\r\n\r\n<p><strong>Ford Escape ra mắt lần đầu ti&ecirc;n tại thị trường Việt Nam v&agrave;o th&aacute;ng 10/2001</strong>. Đ&acirc;y cũng ch&iacute;nh l&agrave; mẫu xe đặt nền m&oacute;ng đầu ti&ecirc;n để mở ra ph&acirc;n kh&uacute;c Crossover 5 chỗ hạng C ho&agrave;n to&agrave;n mới tại nước ta.</p>\r\n\r\n<p>Ford Escape thời điểm mới ra mắt được trang bị khối động cơ V6 3.0L, hộp số c&oacute; 2 tuỳ chọn gồm số s&agrave;n 5 cấp hoặc tự động 4 cấp. Đi k&egrave;m l&agrave; tuỳ chọn dẫn động 1 hoặc 2 cầu. Nhờ cấu h&igrave;nh khủng n&agrave;y, Ford Escape c&oacute; thể sản sinh c&ocirc;ng suất tối đa 200 m&atilde; lực.&nbsp;</p>\r\n', 0, 100, '1657442646_vn-wall-mount-outdoor-ar24tyhycwkxsv-f-ar24tyhycw20-217615165.jpg', 2820010000, 17, 8, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (13, 'Hyundai Grand I10', '<h2>Tại thị trường Ấn Độ, Hyundai Grand i10 l&agrave; mẫu hatchback b&aacute;n chạy nhất với doanh số b&aacute;n hơn 10.000 xe mỗi th&aacute;ng. Để c&oacute; thể duy tr&igrave; được đ&agrave; tăng trưởng n&agrave;y, Hyundai đ&atilde; quyết định sẽ cho ra mắt&nbsp;<strong>Hyundai Grand i10 2022&nbsp;</strong>với nhiều n&acirc;ng cấp đắt gi&aacute;.&nbsp;</h2>\r\n', '<p>Hyundai cũng hy vọng rằng Grand i10 2022 c&oacute; thể tiếp tục nới rộng khoảng c&aacute;ch với c&aacute;c đối thủ&nbsp;<a href=\"https://xetot.com/toan-quoc/mua-ban-oto/suzuki--swift\" rel=\"noopener noreferrer\" target=\"_blank\" title=\"Giá xe Suzuki Swift 2020\">Suzuki Swift</a>&nbsp;v&agrave; Ford Figo trong ph&acirc;n kh&uacute;c hatchback tại thị trường Ấn Độ.<strong>&nbsp;</strong></p>\r\n\r\n<p><strong>Hyundai Grand i10&nbsp;2022&nbsp;bản NIOS c&oacute; gi&aacute; khởi điểm 9.700 USD</strong>. Hyundai Grand i10 NIOS c&oacute; 6 t&ugrave;y chọn m&agrave;u sắc ngoại thất gồm: đỏ, trắng, bạc, x&aacute;m, xanh v&agrave; xanh &aacute;nh kim.</p>\r\n', 0, 100, '1657442105_vn-stick-handy-vs15r8544s1-vs15r8544s1-sv-frontblue-216174216.jpg', 5519560000, 35, 16, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (14, 'Ford Ranger Hybrid', '<h2><em>Ford Ranger Hybrid 2024 l&agrave; phi&ecirc;n bản hybrid sạc điện PHEV của mẫu b&aacute;n tải cỡ trung Mỹ. Xe sở hữu ngoại h&igrave;nh vạm vỡ đậm chất Mỹ c&ugrave;ng tầm vận h&agrave;nh thuần điện l&ecirc;n đến 45 km.</em></h2>\r\n', '<p>Ng&agrave;y 19/09-2023, phi&ecirc;n bản Hybrid sạc điện PHEV của Ford Ranger l&agrave;&nbsp;<strong>Ranger Hybrid 2024 ch&iacute;nh thức được c&ocirc;ng bố với người ti&ecirc;u d&ugrave;ng.</strong></p>\r\n\r\n<p>Theo những th&ocirc;ng tin h&atilde;ng xe cung cấp, c&oacute; thể thấy diện mạo của Ford Ranger Hybrid 2024 kh&ocirc;ng kh&aacute;c biệt nhiều so với bản sử dụng động cơ đốt trong đang b&aacute;n tr&ecirc;n thị trường hiện nay. Xe duy tr&igrave; thiết kế mạnh mẽ, vạm vỡ đặc trưng.</p>\r\n\r\n<p>Vượt trội hơn cả trong lần xuất hiện n&agrave;y của Ford Ranger Hybrid 2024 nằm ở khả năng vận h&agrave;nh thuần điện l&ecirc;n đến 45 km.</p>\r\n\r\n<p>Cũng theo th&ocirc;ng tin được cung cấp bởi h&atilde;ng xe, Ford Ranger Hybrid 2024 sẽ được đưa v&agrave;o sản xuất từ cuối năm 2024, với đợt giao h&agrave;ng đầu ti&ecirc;n dự kiến v&agrave;o đầu năm 2025.</p>\r\n', 1, 100, '1657441919_vn-robot-vr05r5050wk-vr05r5050wk-sv-519428263.jpg', 9137800000, 39, 8, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (15, 'VinFast VF5', '<h2>Với thiết kế hiện đại, độc đ&aacute;o, được trang bị c&aacute;c c&ocirc;ng nghệ v&agrave; t&iacute;nh năng&nbsp;th&ocirc;ng minh&nbsp;vượt trội, khả năng vận h&agrave;nh mạnh mẽ, an to&agrave;n,&nbsp;VF 5&nbsp;hội tụ đầy đủ c&aacute;c yếu tố người d&ugrave;ng trẻ t&igrave;m kiếm cho một chiếc xe điện đ&ocirc; thị l&yacute; tưởng.</h2>\r\n', '<p>VF 5 sở hữu thiết kế hiện đại, trẻ trung, c&aacute; t&iacute;nh v&agrave; nổi bật với c&aacute;c lựa chọn phối m&agrave;u nội ngoại thất, đảm bảo c&aacute; nh&acirc;n h&oacute;a theo phong c&aacute;ch sống, c&aacute; t&iacute;nh v&agrave; sở th&iacute;ch của mỗi kh&aacute;ch h&agrave;ng.</p>\r\n', 0, 100, '1657439198_vn-microwaves-oven-grill-mg23t5018ck-324512-mg23t5018ce-sv-505450992.jpg', 9630290000, 25, 10, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (16, 'Toyota Camry', '<h2>Chiếc xe gắn liền với nhiều cơ quan c&ocirc;ng quyền với kh&ocirc;ng gian rộng, nhiều trang bị tiện nghị cho h&agrave;ng ghế thứ hai, doanh số mẫu n&agrave;y lu&ocirc;n ổn định mặc d&ugrave; c&oacute; giai đoạn đi xuống nhưng Camry vẫn l&agrave; mẫu xe được y&ecirc;u th&iacute;ch trong ph&acirc;n kh&uacute;c sedan cỡ D.</h2>\r\n', '<h2><strong>Bảng gi&aacute; Toyota Camry 2022</strong></h2>\r\n\r\n<p>Tại Việt Nam, Toyota Camry 2022 được ph&acirc;n ph&acirc;n phối ch&iacute;nh h&atilde;ng 4 phi&ecirc;n bản. Gi&aacute; lăn b&aacute;nh tham khảo như sau:</p>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<th>T&ecirc;n phi&ecirc;n bản</th>\r\n			<th>Gi&aacute; ni&ecirc;m yết</th>\r\n			<th>Lăn b&aacute;nh tại HN</th>\r\n			<th>Lăn b&aacute;nh tại TP.HCM</th>\r\n			<th>Lăn b&aacute;nh tại H&agrave; Tĩnh</th>\r\n			<th>Lăn b&aacute;nh tại c&aacute;c tỉnh kh&aacute;c</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>2.0G</td>\r\n			<td>1 tỷ 070 triệu VNĐ</td>\r\n			<td>1.220.737.000 VNĐ</td>\r\n			<td>1.199.337.000 VNĐ</td>\r\n			<td>1.191.037.000 VNĐ</td>\r\n			<td>1.180.337.000 VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2.0Q</td>\r\n			<td>1 tỷ 185 triệu VNĐ</td>\r\n			<td>1.349.537.000 VNĐ</td>\r\n			<td>1.325.837.000 VNĐ</td>\r\n			<td>1.318.687.000 VNĐ</td>\r\n			<td>1.306.837.000 VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2.5Q</td>\r\n			<td>1 tỷ 370 triệu VNĐ</td>\r\n			<td>1.556.737.000 VNĐ</td>\r\n			<td>1.529.337.000 VNĐ</td>\r\n			<td>1.524.037.000 VNĐ</td>\r\n			<td>1.510.337.000 VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2.5HV</td>\r\n			<td>1 tỷ 460 triệu VNĐ</td>\r\n			<td>1.657.537.000 VNĐ</td>\r\n			<td>1.628.337.000 VNĐ</td>\r\n			<td>1.623.937.000 VNĐ</td>\r\n			<td>1.609.337.000 VNĐ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 0, 100, '1657439027_vn-bespoke-mw8000r-mc35r8088le-sv-505437378.jpg', 1238050000, 25, 1, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (17, 'Land Cruiser 2022 bản 4 chỗ', '<h2><em>Land Cruiser 2022&nbsp;bản 4 chỗ nhập khẩu Trung Đ&ocirc;ng được h&atilde;ng MBS l&agrave;m lại khoang nội thất sang trọng với 4 ghế ngồi, c&ugrave;ng với đ&oacute; l&agrave; t&iacute;ch hợp nhiều tiện nghi hiện đại. So với c&aacute;c phi&ecirc;n bản Land Cruiser ti&ecirc;u chuẩn, phi&ecirc;n bản n&agrave;y kh&ocirc;ng c&oacute; kh&aacute;c biệt đ&aacute;ng ch&uacute; &yacute; về thiết kế ngoại thất.</em></h2>\r\n', '<h3><span style=\"font-size:14px\">M</span>BS l&agrave; h&atilde;ng độ nội thất nổi tiếng tr&ecirc;n thế giới, chuy&ecirc;n n&acirc;ng cấp tiện nghi ở h&agrave;ng ghế sau tr&ecirc;n c&aacute;c mẫu xe sang như&nbsp;<a href=\"https://muaxegiatot.com/xe-lexus-lx600.html\" rel=\"noopener\" target=\"_blank\" title=\"Giá xe Lexus LX600\">Lexus LX600</a>, Mercedes-Benz G-Class, V-Class v&agrave; GLS. Land Cruiser 2022 bản 4 chỗ l&agrave; một dự &aacute;n n&acirc;ng cấp mới nhất của MBS.</h3>\r\n', 0, 100, '1657437648_vn-the-premiere-lsp9t-sp-lsp9tkaxxv-414047979.jpg', 5299390000, 5, 14, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (18, 'Audi Q3', '<p>Q3 l&agrave; chiếc SUV 5 chỗ cỡ nhỏ hạng sang, nơi nhiều đối thủ như Mercedes GLA, BMW X1 tham chiến. Q3 ngo&agrave;i bản thiết kế SUV truyền thống, xe c&ograve;n c&oacute; th&ecirc;m biến thể kiểu d&aacute;ng coupe mang t&ecirc;n Q3 Sportback.&nbsp;</p>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p>Mẫu SUV cỡ nhỏ hạng sang bản ch&iacute;nh h&atilde;ng về Việt Nam, thiết kế thể thao hơn, lắp động cơ 1.5, b&aacute;n ra hai phi&ecirc;n bản.</p>\r\n\r\n<p>Trung tuần th&aacute;ng 4/2020, Audi giới thiệu Q3 mới.&nbsp;Mẫu SUV cỡ nhỏ hạng sang mới thiết kế lại lưới tản nhiệt lớn hơn, kiểu khung đơn h&igrave;nh b&aacute;t gi&aacute;c ph&acirc;n chia bởi những thanh dọc cũng như hai hốc gi&oacute; hai b&ecirc;n. C&aacute;c bề mặt với những đường g&acirc;n dập nổi, tổng thể thiết kế thể thao v&agrave; mạnh mẽ hơn.</p>\r\n\r\n<p>Đ&egrave;n pha LED mảnh hướng v&agrave;o trong với định dạng chữ V. Tr&ecirc;n bản Q3 Advanced v&agrave; S line trang bị đ&egrave;n pha LED Matrix ti&ecirc;u chuẩn. Phần đu&ocirc;i tạo h&igrave;nh khối chia hai phần bởi một đường ngang. Dải đ&egrave;n hậu LED h&igrave;nh chữ L nằm, bộ khuếch t&aacute;n hầm hố, ốp trang tr&iacute; ống xả.</p>\r\n\r\n<p>So với bản cũ, Q3 mới tăng k&iacute;ch thước. Xe d&agrave;i 4.484 mm (tăng 96 mm), rộng 1.849 mm (tăng 18 mm), cao 1.585 mm (giảm 5 mm). Chiều d&agrave;i cơ sở cũng tăng th&ecirc;m 77 mm đạt 2.680 mm.</p>\r\n', 0, 100, '1657437411_vn-the-freestyle-lsp3b-sp-lsp3blaxxv-530663510.jpg', 3282790000, 7, 17, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (19, 'KIA Carnival ', '<h2><span style=\"font-size:16px\"><strong>T</strong></span>rong th&aacute;ng 02/2022, lượng&nbsp;<a href=\"https://xetot.com/toan-quoc/mua-ban-oto\" rel=\"noopener\" target=\"_blank\" title=\"xe ô tô bán ra toàn thị trường\">xe &ocirc; t&ocirc; b&aacute;n ra to&agrave;n thị trường</a>&nbsp;giảm mạnh, thứ hạng xe b&aacute;n chạy nhất th&aacute;ng cũng c&oacute; sự x&aacute;o trộn. Đ&aacute;ng ch&uacute; &yacute;, KIA Carnival lần đầu g&oacute;p mặt trong danh s&aacute;ch xe b&aacute;n chạy của th&aacute;ng.</h2>\r\n', '<h3>Mẫu SUV cỡ B Toyota Corolla Cross bất ngờ vươn l&ecirc;n dẫn đầu to&agrave;n thị trường với doanh số 1.358 xe b&aacute;n ra trong th&aacute;ng 2 vừa qua. Trước đ&oacute; v&agrave;o hồi th&aacute;ng 11 v&agrave; th&aacute;ng 12 năm 2021, mẫu xe n&agrave;y từng được đứng ở vị tr&iacute; thứ 3 trong Top 10 xe b&aacute;n chạy nhất th&aacute;ng. C&oacute; thể thấy, Corolla Cross đang dần dần trở th&agrave;nh &ldquo;con g&agrave; đẻ trứng v&agrave;ng&rdquo; của h&atilde;ng xe Nhật Bản, thay thế cho &ldquo;người anh em&rdquo; Toyota Vios. Hiện xe đang c&oacute; gi&aacute; b&aacute;n từ 720-910 triệu đồng.</h3>\r\n', 0, 100, '1657433062_vn-soundbar-hw-s61b-hw-s61b-xv-531573869.jpg', 9515770000, 0, 5, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (20, 'Honda Brio', '<h2>Honda Brio l&agrave; mẫu Hatchback đ&ocirc; thị cỡ nhỏ xuất hiện lần đầu v&agrave;o năm 2010 v&agrave; được thương hiệu Nhật Bản định vị hướng v&agrave;o nh&oacute;m kh&aacute;ch h&agrave;ng phổ th&ocirc;ng của những thị trường mới nổi như Th&aacute;i Lan, Ấn Độ.&nbsp;</h2>\r\n', '<p>Honda Brio xuất hiện với một vẻ ngo&agrave;i trẻ trung, bắt mắt. Tuy tr&ocirc;ng to lớn hơn đ&ocirc;i ch&uacute;t so với đối thủ mạnh nhất ph&acirc;n kh&uacute;c n&agrave;y l&agrave; Hyundai Grand I10 nhưng Brio lại thua k&eacute;m về chiều d&agrave;i trục cơ sở.&nbsp;</p>\r\n', 0, 100, '1657432829_vn-q-series-soundbar-hw-q700b-hw-q700b-xv-531574180.jpg', 8730500000, 5, 14, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (21, ' bộ đôi Peugeot 3008 va 5008', '<h2>Mới đ&acirc;y, nh&agrave; ph&acirc;n phối Thaco Trường Hải đ&atilde; ch&iacute;nh thức ra mắt thị trường Việt 2 phi&ecirc;n bản gi&aacute; rẻ mới của bộ đ&ocirc;i SUV 5 chỗ v&agrave; 7 chỗ của h&atilde;ng xe Ph&aacute;p l&agrave;<strong>&nbsp;Peugeot 3008 Active v&agrave; Peugeot 5008 Active</strong>&nbsp;được cắt giảm nhiều option để đem đến mức gi&aacute; giảm 50-150 triệu đồng, ph&acirc;n phối song song với c&aacute;c bản &ldquo;full option&rdquo; trước đ&acirc;y l&agrave; Peugeot 3008 (1.149 tỷ đồng) v&agrave; Peugeot 5008 (1.349 tỷ đồng).</h2>\r\n', '<p>Bước đi mới n&agrave;y nhằm bổ sung th&ecirc;m sự lựa chọn, gi&uacute;p kh&aacute;ch h&agrave;ng Việt dễ d&agrave;ng tiếp cận được những sản phẩm cao cấp của h&atilde;ng với gi&aacute; cả hợp l&yacute; m&agrave; vẫn giữ được những gi&aacute; trị tinh t&uacute;y nhất của của Peugeot to&agrave;n cầu.</p>\r\n\r\n<p>Tại Việt Nam, mẫu SUV 7 chỗ Peugeot 5008 Active được xếp &ldquo;chung m&acirc;m&rdquo; với Toyota Fortuner, Ford Everest, Hyundai SantaFe; trong khi đ&oacute;, Peugeot 3008 Active 2022 nằm c&ugrave;ng ph&acirc;n kh&uacute;c SUV 5 chỗ ngồi như Mazda CX-5, Honda CR-V, Nissan X-Trail v&agrave; Tucson.</p>\r\n', 0, 100, '1657432344_vn-serif-tv-ls01t-qa55ls01takxxv-frontwhite-221737538.jpg', 5105170000, 35, 17, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (22, 'Ford Everest Ambiente', '<p>Ford ra mắt Everest thế hệ mới tại Việt Nam h&ocirc;m 1/7, với&nbsp;4 phi&ecirc;n bản, 3&nbsp;t&ugrave;y chọn&nbsp;Ambiente, Sport, Titanium đều dẫn động một cầu, turbo đơn&nbsp;v&agrave; Titanium+ dẫn động 2 cầu, turbo k&eacute;p.</p>\r\n\r\n<h2><strong>Xe</strong>&nbsp;<strong>Ford Everest​&nbsp;2023&nbsp;gi&aacute; bao nhi&ecirc;u?</strong></h2>\r\n', '<p>Hiện tr&ecirc;n trang web ch&iacute;nh thức của Ford Việt Nam đang cập nhật&nbsp;<strong>bảng gi&aacute; xe Ford</strong>&nbsp;Everest 2023 như sau:</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"3\">Bảng gi&aacute; Ford Everest 2023</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Phi&ecirc;n bản</strong></td>\r\n			<td><strong>M&agrave;u sắc</strong></td>\r\n			<td><strong>Gi&aacute; xe (tỷ đồng)</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Everest Wildtrak 2.0L AT 4&times;4</td>\r\n			<td>M&agrave;u đen</td>\r\n			<td>1,499</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Everest Wildtrak 2.0L AT 4&times;4</td>\r\n			<td>M&agrave;u Trắng tuyết/ M&agrave;u Đỏ cam/ M&agrave;u V&agrave;ng Luxe</td>\r\n			<td>1,506</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Everest Titanium+ 2.0L AT 4&times;4</td>\r\n			<td>M&agrave;u kh&aacute;c</td>\r\n			<td>1,468</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Everest Titanium+ 2.0L AT 4&times;4</td>\r\n			<td>M&agrave;u Trắng tuyết / M&agrave;u Đỏ cam</td>\r\n			<td>1,475</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Everest Titanium+ 2.0L AT 4&times;4</td>\r\n			<td>Nội thất da m&agrave;u hạt dẻ</td>\r\n			<td>1,475</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Everest Titanium+ 2.0L AT 4&times;4</td>\r\n			<td>M&agrave;u Trắng tuyết /<br />\r\n			M&agrave;u Đỏ cam kết hợp với Nội thất da m&agrave;u hạt dẻ</td>\r\n			<td>1,482</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Everest Titanium 2.0L AT 4&times;2</td>\r\n			<td>M&agrave;u kh&aacute;c</td>\r\n			<td>1,299</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 0, 100, '1657419885_vn-the-frame-qa75ls03bakxxv-front-black-531139141.jpg', 8334370000, 5, 8, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (23, 'Volkswagen T Cross', '<h2>Ng&agrave;y 17/3 vừa qua, Volkswagen Việt Nam đưa ra th&ocirc;ng b&aacute;o nhận&nbsp;<strong>đặt h&agrave;ng mẫu xe Volkswagen T-Cross 2022 qua h&igrave;nh thức showroom online</strong>. Đến thời điểm hiện tại, gi&aacute; b&aacute;n vẫn chưa được lộ. Được biết, xe sẽ ra mắt v&agrave;o th&aacute;ng 4/2022 trước khi giao đến kh&aacute;ch h&agrave;ng trong th&aacute;ng 5 v&agrave; th&aacute;ng 6 năm 2022. Như vậy T-Cross 2022 trở th&agrave;nh&nbsp;<a href=\"https://muaxegiatot.com/gia-xe-suv-co-b.html\" rel=\"noopener\" target=\"_blank\" title=\"mẫu SUV hạng B\">mẫu SUV hạng B</a>&nbsp;đầu ti&ecirc;n của Volkswagen tại thị trường Việt Nam.</h2>\r\n', '<h3>V&igrave; thế, ngay sau khi được ra mắt đến nay, mẫu SUV cỡ B n&agrave;y đ&atilde; được ph&acirc;n phối tại nhiều thị trường kh&aacute;c nhau tr&ecirc;n thế giới, bao gồm cả Đ&ocirc;ng Nam &Aacute; như Indonesia, Singapore, Philippines. V&agrave; trong th&aacute;ng 4 sắp tới sẽ được ra mắt tại Việt Nam.</h3>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 100, '1657418600_vn-qled-qn85b-qa65qn85bakxxv-530992953.jpg', 6856330000, 7, 11, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (24, 'Hyundai Ioniq 5', '<h2>Ng&agrave;y 31/7/2023, Hyundai Th&agrave;nh C&ocirc;ng ch&iacute;nh thức ph&acirc;n phối Ioniq 5 - d&ograve;ng &ocirc;t&ocirc; điện đầu ti&ecirc;n của h&atilde;ng ở Việt Nam. Hyundai Ioniq 5 lắp r&aacute;p tại Việt Nam. Hyundai định vị Ioniq 5 cạnh tranh với VinFast VF 8.</h2>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p>Hơn một năm đưa về thăm d&ograve; thị trường,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/hang-xe/hyundai-9\" rel=\"dofollow\">Hyundai</a>&nbsp;Th&agrave;nh C&ocirc;ng ch&iacute;nh thức ph&acirc;n phối Ioniq 5. D&ograve;ng &ocirc;t&ocirc; điện đầu ti&ecirc;n của Hyundai lắp r&aacute;p tại Việt Nam với 2 phi&ecirc;n bản, gồm Exclusive gi&aacute; 1,3 tỷ v&agrave; Prestige gi&aacute; 1,45 tỷ đồng.&nbsp;Hyundai Ioniq 5 2023&nbsp;nằm c&ugrave;ng cỡ với&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/vinfast-vf-8-208\" rel=\"dofollow\">VinFast VF 8</a>. Mẫu xe điện thương hiệu Việt c&oacute; 2 phi&ecirc;n bản, gồm Eco gi&aacute; 1,094 tỷ v&agrave; Plus gi&aacute; 1,263 tỷ đồng chưa pin. Nếu c&oacute; pin, gi&aacute; xe VF 8 l&agrave; 1,29 tỷ v&agrave; 1,47 tỷ. Như vậy, gi&aacute; của Ioniq 5 v&agrave; VF 8 l&agrave; tương đương nhau.</p>\r\n', 0, 100, '1657418255_vn-qled-qn700b-qa65qn700bkxxv-531504242.jpg', 8778530000, 5, 16, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (25, 'Line-Up SUV', '<h2>Thu h&uacute;t mọi &aacute;nh nh&igrave;n bằng một thiết kế hiện đại, thể thao, Kia Sonet sở hữu lưới tản nhiệt mũi hổ đặc trưng c&ugrave;ng với đ&egrave;n LED Heart Beat đặc trưng của h&atilde;ng xe H&agrave;n Quốc.</h2>\r\n', '<p>Nội thất Kia Sonet được đầu tư chỉn chu với t&aacute;p-l&ocirc; kiểu đối xứng, trang tr&iacute; ốp nhựa v&agrave; giả da, v&ocirc;-lăng v&aacute;t đ&aacute;y thể thao c&ugrave;ng cụm đồng hồ kỹ thuật số. Xe sở hữu c&aacute;c tiện nghi hấp dẫn như m&agrave;n h&igrave;nh giải tr&iacute; 8 inch, điều h&ograve;a tự động, d&agrave;n &acirc;m thanh 6 loa, cửa sổ trời.</p>\r\n\r\n<p>Dưới nắp capo Kia Sonet l&agrave; động cơ xăng Smartstream 1.5L, sản sinh 113 m&atilde; lực v&agrave; 144 Nm, kết hợp với hộp số v&ocirc; cấp. Xe hỗ trợ 3 chế độ l&aacute;i v&agrave; 3 chế độ địa h&igrave;nh kh&aacute;c nhau.&nbsp;</p>\r\n\r\n<p>Điểm đặc biệt nữa của mẫu SUV đ&ocirc; thị cỡ nhỏ nh&agrave; Kia l&agrave; thiết kế khoảng s&aacute;ng gầm xe cao đến 205 mm &ndash; cực kỳ năng động tr&ecirc;n c&aacute;c địa h&igrave;nh phức tạp của đ&ocirc; thị Việt Nam.&nbsp;</p>\r\n\r\n<p>Kia Sonet đang được ph&acirc;n phối tại Việt Nam theo h&igrave;nh thức lắp r&aacute;p trong nước, bao gồm 4 phi&ecirc;n bản c&oacute;&nbsp;<a href=\"https://muaxegiatot.com/gia-xe-kia.html\" rel=\"noopener\" target=\"_blank\" title=\"giá bán xe KIA\">gi&aacute; b&aacute;n xe KIA</a>&nbsp;n&agrave;y từ 499 &ndash; 624 triệu đồng.&nbsp;</p>\r\n', 0, 100, '1657271332_active2nhom.jpg', 3823660000, 5, 5, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (26, 'Hyundai Tucson', '<p>Mẫu Tucson thế hệ mới xe giữ nguy&ecirc;n 4 phi&ecirc;n bản, th&ecirc;m t&ugrave;y chọn 4 b&aacute;nh v&agrave; th&ecirc;m g&oacute;i t&iacute;nh năng an to&agrave;n chủ động.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p>Cuối th&aacute;ng 12/2021, TC Motor tung ra thị trường mẫu Hyundai Tucson 2022 với h&agrave;ng loạt thay đổi ở thiết kế v&agrave; n&acirc;ng cấp t&iacute;nh năng, c&ocirc;ng nghệ cũng như khả năng vận h&agrave;nh. Trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/phan-khuc/phan-khuc-c-4#crossover\" rel=\"dofollow\">crossover cỡ C</a>, Hyundai Tucson đứng thứ hai về doanh số, sau&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/mazda-cx-5-100\" rel=\"dofollow\">Mazda CX-5</a>&nbsp;v&agrave; xếp tr&ecirc;n c&aacute;c đối thủ như&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/honda-cr-v-44\" rel=\"dofollow\">Honda CR-V</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/mitsubishi-outlander-122\" rel=\"dofollow\">Mitsubishi Outlander</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/kia-sportage-206\" rel=\"dofollow\">Kia Sportage</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/peugeot-3008-128\" rel=\"dofollow\">Peugeot 3008</a>.</p>\r\n\r\n<p>Mẫu crossover n&agrave;y vẫn c&oacute; bốn lựa chọn phi&ecirc;n bản như thế hệ trước, với c&aacute;c lựa chọn động cơ xăng v&agrave; dầu c&ugrave;ng t&ugrave;y chọn 7 m&agrave;u sắc.</p>\r\n', 0, 100, '1657274325_watchlte44.jpg', 1782710000, 15, 16, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (27, 'Hyundai Stargazer', '<h2>Stargazer l&agrave; chiếc MPV cỡ nhỏ m&agrave; Hyundai Th&agrave;nh C&ocirc;ng b&aacute;n ra để cạnh tranh c&aacute;c đối thủ Mitsubishi Xpander, Toyota Veloz, Suzuki Ertiga...</h2>\r\n', '<h2><strong>Bảng gi&aacute; Hyundai Stargazer 2022</strong></h2>\r\n\r\n<p>Tại Việt Nam, Hyundai Stargazer 2022 được ph&acirc;n ph&acirc;n phối ch&iacute;nh h&atilde;ng 4 phi&ecirc;n bản. Gi&aacute; lăn b&aacute;nh tham khảo như sau:</p>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<th>T&ecirc;n phi&ecirc;n bản</th>\r\n			<th>Gi&aacute; ni&ecirc;m yết</th>\r\n			<th>Lăn b&aacute;nh tại HN</th>\r\n			<th>Lăn b&aacute;nh tại TP.HCM</th>\r\n			<th>Lăn b&aacute;nh tại H&agrave; Tĩnh</th>\r\n			<th>Lăn b&aacute;nh tại c&aacute;c tỉnh kh&aacute;c</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>1.5 Ti&ecirc;u chuẩn</td>\r\n			<td>575 triệu VNĐ</td>\r\n			<td>666.694.000 VNĐ</td>\r\n			<td>655.194.000 VNĐ</td>\r\n			<td>641.944.000 VNĐ</td>\r\n			<td>636.194.000 VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>1.5 Đặc biệt</td>\r\n			<td>625 triệu VNĐ</td>\r\n			<td>722.694.000 VNĐ</td>\r\n			<td>710.194.000 VNĐ</td>\r\n			<td>697.444.000 VNĐ</td>\r\n			<td>691.194.000 VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>1.5 Cao cấp</td>\r\n			<td>675 triệu VNĐ</td>\r\n			<td>778.694.000 VNĐ</td>\r\n			<td>765.194.000 VNĐ</td>\r\n			<td>752.944.000 VNĐ</td>\r\n			<td>746.194.000 VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>1.5 Cao cấp 6 chỗ</td>\r\n			<td>685 triệu VNĐ</td>\r\n			<td>789.894.000 VNĐ</td>\r\n			<td>776.194.000 VNĐ</td>\r\n			<td>764.044.0</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 0, 100, '1657273893_watch4.jpg', 6442560000, 15, 16, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (28, 'Hyundai Palisade', '<h2>Mẫu xe gầm cao cỡ E lần đầu ra mắt thị trường Việt Nam, lắp r&aacute;p trong nước, b&aacute;n ra 4 phi&ecirc;n bản, lắp một động cơ dầu 2.2.</h2>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p>Hyundai ra mắt Palisade cho thị trường Việt Nam ng&agrave;y 15/9. Palisade l&agrave; mẫu SUV 3 h&agrave;ng ghế lớn v&agrave; đắt nhất của&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/hang-xe/hyundai-9\" rel=\"dofollow\">Hyundai</a>, cạnh tranh với những đối thủ đ&aacute;ng gờm như&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/ford-explorer-202\" rel=\"dofollow\">Ford&nbsp;Explorer</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/volkswagen-teramont-191\" rel=\"dofollow\">Volkswagen Teramont</a>. Palisade bổ sung th&ecirc;m d&ograve;ng sản phẩm xe gầm cao của Hyundai, b&ecirc;n cạnh Santa Fe, Tucson.</p>\r\n\r\n<p>Ngoại h&igrave;nh Palisade hầm hố, xe d&agrave;i 4.995 mm, rộng 1.975 mm, cao 1.785 mm v&agrave; chiều d&agrave;i cơ sở 2.900 mm. Khoảng s&aacute;ng gầm 203 mm. Lưới tản nhiệt k&iacute;ch thước lớn, tạo h&igrave;nh th&aacute;c nước, đ&egrave;n pha LED, đ&egrave;n ban ng&agrave;y LED đặc trưng bao quanh lưới tản nhiệt, tạo th&agrave;nh khối liền mạch. V&agrave;nh 18 hoặc 20 inch t&ugrave;y phi&ecirc;n bản. Cản trước tạo h&igrave;nh kh&iacute; động học. Nắp ca-p&ocirc; tạo h&igrave;nh với đường g&acirc;n nổi. Trụ C thiết kế tr&agrave;n viền giấu cột trụ. Cụm đ&egrave;n hậu LED to bản chạy dọc, cản sau k&iacute;ch thước lớn, ống xả k&eacute;p thể thao.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 100, '1657270948_active2.jpg', 7364690000, 5, 16, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (30, 'Hyundai Venue', '<p>Venue thuộc ph&acirc;n kh&uacute;c A, mẫu xe c&ograve;n thiếu trong dải sản phẩm crossover/SUV của Hyundai, b&ecirc;n cạnh những Creta (cỡ B), Tucson (cỡ C), Santa Fe (cỡ D), Palisade (cỡ E).</p>\r\n\r\n<p>Tại thị trường Việt Nam, t&acirc;n binh Venue cạnh tranh với đối thủ Kia Sonet v&agrave; Toyota Raize. Mẫu xe gầm cao cỡ A của Hyundai chia sẻ nền tảng khung gầm với Kia Sonet. Venue cũng l&agrave; sản phẩm to&agrave;n cầu của h&atilde;ng xe H&agrave;n Quốc.</p>\r\n', '<h2><strong>Th&ocirc;ng số kỹ thuật cơ bản</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Động cơ</strong></p>\r\n\r\n	<p>I3 Kappa 1.0 Turbo GDI</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>C&ocirc;ng suất (m&atilde; lực)</strong></p>\r\n\r\n	<p>120/6.000</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>M&ocirc;-men xoắn (Nm)</strong></p>\r\n\r\n	<p>172/1.500-4.000</p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Hộp số</strong></p>\r\n\r\n	<p>7DCT, DCT 7 cấp</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Hệ dẫn động</strong></p>\r\n\r\n	<p>FWD</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Số chỗ</strong></p>\r\n\r\n	<p>5</p>\r\n	</li>\r\n</ul>\r\n', 0, 100, '1657270308_fit2.jpg', 7495750000, 30, 16, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (32, 'MG5', '<h2><em>MG 5 2023 l&agrave; c&aacute;i t&ecirc;n mới nhất của thương hiệu MG tại thị trường Việt Nam, ch&iacute;nh thức gia nhập từ th&aacute;ng 2 năm 2023. Hiện mẫu sedan hạng C n&agrave;y đang được mở b&aacute;n với 2 phi&ecirc;n bản.&nbsp;</em></h2>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3><strong>Giới thiệu chung</strong></h3>\r\n\r\n<p>Thế hệ đầu ti&ecirc;n của MG 5 ra mắt v&agrave;o th&aacute;ng 3 năm 2012 do MG Motor ph&aacute;t triển, doanh nghiệp n&agrave;y hiện do SAIC, tập đo&agrave;n Trung Quốc nắm quản l&yacute;. Theo đ&oacute; tại Trung Quốc, MG 5 được mở b&aacute;n với biến thể hatchback. Trong khi, ở một số thị trường kh&aacute;c ph&acirc;n phối biến thể sedan với t&ecirc;n gọi l&agrave; MG GT.&nbsp;</p>\r\n\r\n<p><strong>Thế hệ thứ 2 của MG 5&nbsp;</strong>tiếp tục được&nbsp;<a href=\"https://tapchicongthuong.vn/bai-viet/mg5-2021-moi-lo-dien-truoc-khi-ra-mat-chinh-thuc-75316.htm\" rel=\"noopener\" target=\"_blank\">ra mắt tại Triển l&atilde;m &ocirc; t&ocirc; Bắc Kinh 2020</a>. Sau đ&oacute;, MG 5 bắt đầu tiếp cận thị trường Đ&ocirc;ng Nam &Aacute;, mở đầu l&agrave; tại Th&aacute;i Lan.&nbsp;</p>\r\n', 1, 100, '1657436740_samsung-galaxy-z-fold3-5g-xanh-reu-2.jpg', 5384670000, 20, 21, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (33, 'Audi Q6 e-tron', '<h2>Đối với những gia đ&igrave;nh cần một chiếc xe rộng r&atilde;i, di chuyển &ecirc;m &aacute;i, nhẹ nh&agrave;ng v&agrave; theo xu hướng tương lai th&igrave;&nbsp;<strong>xe điện Audi Q6 e-tron 2024</strong>&nbsp;l&agrave; lựa chọn tuyệt vời thời điểm n&agrave;y. Kh&ocirc;ng chỉ đ&aacute;p ứng những nhu cầu tr&ecirc;n, Audi Q6 e-tron c&ograve;n sở hữu thiết kế khiến nhiều người &ldquo;m&ecirc; mẩn&rdquo;.</h2>\r\n', '<table border=\"2\" cellpadding=\"2\" cellspacing=\"2\">\r\n	<thead>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>Số chỗ ngồi</p>\r\n			</td>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>5</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>Chiều D&agrave;i x Rộng x Cao (mm)</p>\r\n			</td>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>4.962 x 1.964 x 1.667</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>Chiều d&agrave;i cơ sở (mm)</p>\r\n			</td>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>2960</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>Động cơ</p>\r\n			</td>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<ul>\r\n				<li>2 động cơ điện</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>C&ocirc;ng suất (m&atilde; lực)</p>\r\n			</td>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<ul>\r\n				<li>375 hoặc 396</li>\r\n				<li>483 hoặc 510</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"1\">\r\n			<p>M&ocirc; men xoắn (Nm)</p>\r\n\r\n			<p><ins><iframe frameborder=\"0\" height=\"0\" id=\"aswift_3\" name=\"aswift_3\" scrolling=\"no\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-3655642957264955&amp;output=html&amp;h=250&amp;slotname=3659613648&amp;adk=2098039687&amp;adf=3338857826&amp;pi=t.ma~as.3659613648&amp;w=300&amp;fwrn=4&amp;lmt=1704509981&