INSERT INTO `products` VALUES (34, 'VinFast VF9', '<h2><a href=\"https://xetot.com/tin-xe/danh-gia-xe-vinfast-vf-9/\" rel=\"noopener\" target=\"_blank\" title=\"Giá xe VinFast VF9 2024\"><strong>VinFast VF9</strong></a>&nbsp;l&agrave; mẫu xe chạy điện của h&atilde;ng xe Việt được giới thiệu c&ocirc;ng ch&uacute;ng c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u. Kh&aacute;ch h&agrave;ng chỉ mới được chi&ecirc;m ngưỡng h&igrave;nh ảnh của xe tại buổi ra mắt hoặc c&aacute;c h&igrave;nh ảnh demo. Hiện tại, hoạt động thử nghiệm xe VinFast VF9 vẫn đang diễn ra lặng lẽ, rất &iacute;t khi thấy được c&aacute;c h&igrave;nh ảnh thực tế của mẫu xe n&agrave;y.</h2>\r\n', '<p>VinFast VF9 bản Eco c&oacute; qu&atilde;ng đường tối đa đi được l&agrave; 485 km, bản Plus l&agrave; 680 km. K&iacute;ch thước tổng thể chiều d&agrave;i x chiều rộng x chiều cao lần lượt l&agrave; 5.120 x 2.000 x 1.721 (mm). Với k&iacute;ch thước n&agrave;y, VF9 được xếp v&agrave;o ph&acirc;n kh&uacute;c SUV full-size, tương tự như Lexus LX 600,&nbsp;<a href=\"https://xetot.com/tin-xe/danh-gia-xe-bmw-x7/\" rel=\"noopener\" target=\"_blank\" title=\"Giá xe BMW X7\">BMW X7</a>, Mercedes-Benz GLS,&hellip;</p>\r\n\r\n<p>VinFast VF9 được trang bị hai động cơ điện, sản sinh c&ocirc;ng suất cực đại 402 m&atilde; lực v&agrave; momen xoắn 640 Nm. C&ugrave;ng với hai động cơ điện n&agrave;y l&agrave; hệ dẫn động bốn b&aacute;nh to&agrave;n thời gian. Ngo&agrave;i ra, VF9 c&oacute; thể tăng tốc từ 0 &ndash; 100 km/h trong 6,5 gi&acirc;y.</p>\r\n', 0, 100, '1657261899_s22.jpg', 3026560000, 28, 10, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (35, 'Lexus LS', '<h2>Mẫu sedan hạng sang cỡ lớn của Lexus cạnh tranh với S-class nhưng giống như GX gi&aacute; cao, trang bị kh&ocirc;ng tương xứng khiến LS kh&aacute; k&eacute;n người mua v&agrave; cũng &iacute;t bắt gặp tr&ecirc;n đường.</h2>\r\n', '<h2><strong>Bảng gi&aacute; Lexus LS 2021</strong></h2>\r\n\r\n<p>Tại Việt Nam, Lexus LS 2021 được ph&acirc;n ph&acirc;n phối ch&iacute;nh h&atilde;ng 2 phi&ecirc;n bản. Gi&aacute; lăn b&aacute;nh tham khảo như sau:</p>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<th>T&ecirc;n phi&ecirc;n bản</th>\r\n			<th>Gi&aacute; ni&ecirc;m yết</th>\r\n			<th>Lăn b&aacute;nh tại HN</th>\r\n			<th>Lăn b&aacute;nh tại TP.HCM</th>\r\n			<th>Lăn b&aacute;nh tại H&agrave; Tĩnh</th>\r\n			<th>Lăn b&aacute;nh tại c&aacute;c tỉnh kh&aacute;c</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>LS500</td>\r\n			<td>7 tỷ 650 triệu VNĐ</td>\r\n			<td>8.590.337.000 VNĐ</td>\r\n			<td>8.437.337.000 VNĐ</td>\r\n			<td>8.494.837.000 VNĐ</td>\r\n			<td>8.418.337.000 VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>LS500h</td>\r\n			<td>8 tỷ 360 triệu VNĐ</td>\r\n			<td>9.385.537.000 VNĐ</td>\r\n			<td>9.218.337.000 VNĐ</td>\r\n			<td>9.282.9</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 100, '1657436716_glass-samsung-galaxy-note-20-1.jpg', 2824450000, 5, 1, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (36, 'Ford Everest 2023', '<h2>Đầu th&aacute;ng 3 vừa qua,&nbsp;Ford Everest 2023 đ&atilde; ra mắt to&agrave;n cầu với sự thay đổi to&agrave;n diện về thiết kế v&agrave; nhận được sự quan t&acirc;m lớn của người d&ugrave;ng. Đặc biệt, Th&aacute;i Lan đ&atilde; l&agrave; thị trường Đ&ocirc;ng Nam &Aacute; đầu ti&ecirc;n đ&oacute;n nhận&nbsp;<a href=\"https://giaxehoi.vn/gia-xe-everest-hang-ford\" rel=\"noopener\" target=\"_blank\" title=\"Ford Everest 2023 All New\">Ford Everest 2023 All New</a>&nbsp;tại Triển l&atilde;m &Ocirc;t&ocirc; Bangkok 2022 v&agrave; Việt Nam dự kiến l&agrave; thị trường tiếp theo.</h2>\r\n', '<h3>Th&ocirc;ng tin n&agrave;y c&agrave;ng c&oacute; cơ sở hơn khi nhiều tư vấn b&aacute;n h&agrave;ng của đại l&yacute; Ford tiết lộ Everest v&agrave; Ranger thế hệ mới sẽ ra mắt Việt Nam v&agrave;o cuối năm 2022. Trong đ&oacute;, Ford Everest 2023 c&oacute; thể sẽ được ra mắt trước, việc b&agrave;n giao xe sẽ diễn ra trong qu&yacute; III/2022. Hiện tại, nhiều đại l&yacute; th&ocirc;ng b&aacute;o đ&atilde; nhận đặt cọc chiếc SUV n&agrave;y.</h3>\r\n', 0, 100, '1657436862_ce593674025f851b260a3de7facac8cb.jpg', 4542560000, 35, 8, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (37, 'Honda Civic Type R', '<h2>Honda Civic Type R l&agrave; mẫu xe b&aacute;n ế nhất thị trường th&aacute;ng 11/2023 chỉ vỏn vẹn 1 chiếc. Tuy nhi&ecirc;n kh&aacute;c với c&aacute;c mẫu xe b&aacute;n ế kh&aacute;c, Civic Type R kh&ocirc;ng phải l&agrave; mẫu xe c&oacute; tiền l&agrave; c&oacute; thể mua được.</h2>\r\n', '<h3>Lượng đặt h&agrave;ng thực tế theo Honda cho biết kh&aacute; lớn nhưng số lượng sản xuất rất hạn chế do đ&acirc;y l&agrave; phi&ecirc;n bản hiệu suất cao đặc biệt. T&iacute;nh đến thời điểm hiện tại chỉ mới c&oacute; 19 chiếc Civic Type R được b&agrave;n giao đến tay kh&aacute;ch h&agrave;ng.&nbsp;</h3>\r\n', 0, 100, '1657261034_a23.jpg', 4239470000, 21, 14, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (38, 'Honda City', '<h2>Honda City<strong>&nbsp;</strong>ra mắt thị trường &ocirc; t&ocirc; Việt Nam v&agrave;o năm 2013 v&agrave; nhanh ch&oacute;ng trở th&agrave;nh đối thủ đ&aacute;ng gờm của loạt mẫu xe thuộc ph&acirc;n kh&uacute;c hạng B thời điểm đ&oacute;.&nbsp;Đến nay, đ&acirc;y vẫn l&agrave; một trong những sản phẩm &quot;trụ cột&quot; của Honda tr&ecirc;n dải đất h&igrave;nh chữ S.</h2>\r\n', '<p>ước v&agrave;o cuộc chiến gi&agrave;nh thị phần v&ocirc; c&ugrave;ng gay gắt với loạt c&aacute;i t&ecirc;n mạnh như&nbsp;Toyota Vios, Hyundai Accent ... Honda City 2023&nbsp;vẫn c&oacute; chỗ đứng ri&ecirc;ng của m&igrave;nh nhờ sở hữu&nbsp;ngoại thất trẻ trung, tr&agrave;n ngập&nbsp;c&ocirc;ng nghệ cũng như&nbsp;tiện nghi vượt trội.</p>\r\n\r\n<h3><img alt=\"Ngoại thất xe Honda City.\" src=\"https://img1.oto.com.vn/2023/07/04/OpzfnMD2/dnb07139-5764.jpg\" title=\"Ngoại thất xe Honda City\" /></h3>\r\n\r\n<p><em><strong><a href=\"https://oto.com.vn/bang-gia-xe-o-to-honda-city-moi-nhat\" title=\"Giá xe Honda City mới nhất\">Gi&aacute; xe Honda City mới nhất</a></strong>&nbsp;th&aacute;ng 1/2024:&nbsp;<strong>Từ 559 triệu đồng</strong></em></p>\r\n\r\n<p>Honda City&nbsp;m&ocirc; h&igrave;nh n&acirc;ng cấp giữa v&ograve;ng đời được cập nhật v&agrave;o ng&agrave;y 4/7/2023 với 3 phi&ecirc;n bản. Theo đ&oacute;,&nbsp;gi&aacute; xe Honda City ni&ecirc;m yết từ 559 triệu đồng đến 609 triệu đồng. Cụ thể:</p>\r\n', 0, 100, '1657261350_a33.jpg', 7069660000, 27, 14, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (39, 'Hyundai Tucson', '<h2>Mẫu Tucson thế hệ mới xe giữ nguy&ecirc;n 4 phi&ecirc;n bản, th&ecirc;m t&ugrave;y chọn 4 b&aacute;nh v&agrave; th&ecirc;m g&oacute;i t&iacute;nh năng an to&agrave;n chủ động.</h2>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p>Cuối th&aacute;ng 12/2021, TC Motor tung ra thị trường mẫu Hyundai Tucson 2022 với h&agrave;ng loạt thay đổi ở thiết kế v&agrave; n&acirc;ng cấp t&iacute;nh năng, c&ocirc;ng nghệ cũng như khả năng vận h&agrave;nh. Trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/phan-khuc/phan-khuc-c-4#crossover\" rel=\"dofollow\">crossover cỡ C</a>, Hyundai Tucson đứng thứ hai về doanh số, sau&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/mazda-cx-5-100\" rel=\"dofollow\">Mazda CX-5</a>&nbsp;v&agrave; xếp tr&ecirc;n c&aacute;c đối thủ như&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/honda-cr-v-44\" rel=\"dofollow\">Honda CR-V</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/mitsubishi-outlander-122\" rel=\"dofollow\">Mitsubishi Outlander</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/kia-sportage-206\" rel=\"dofollow\">Kia Sportage</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/peugeot-3008-128\" rel=\"dofollow\">Peugeot 3008</a>.</p>\r\n\r\n<p>Mẫu crossover n&agrave;y vẫn c&oacute; bốn lựa chọn phi&ecirc;n bản như thế hệ trước, với c&aacute;c lựa chọn động cơ xăng v&agrave; dầu c&ugrave;ng t&ugrave;y chọn 7 m&agrave;u sắc.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 100, '1657263859_s21 ultra 5g.jpg', 3129880000, 5, 16, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (40, 'LEXUS RX 350 PREMIUM ', '<h2><strong><a href=\"https://xelexus.net.vn/lexus-rx350-premium.html\">Lexus RX 350 Premium</a></strong>&nbsp;l&agrave; chiếc xe SUV 5 chỗ hạng sang cỡ vừa của Lexus,&nbsp;<strong>RX 350 Premium</strong>&nbsp;l&agrave; chiếc xe b&aacute;n chạy nhất của Lexus tại Việt Nam hiện nay. Phi&ecirc;n bản Lexus RX 350 Premium 2024 thu h&uacute;t sự ch&uacute; &yacute; nhờ thiết kế tinh tế, nội thất đẳng cấp v&agrave; được trang bị nhiều c&ocirc;ng nghệ an to&agrave;n ti&ecirc;n tiến v&agrave; đặc biệt RX 350 Premium c&oacute; một mức gi&aacute; v&ocirc; c&ugrave;ng hợp l&yacute;.</h2>\r\n', '<p>Gi&aacute; lăn b&aacute;nh xe Lexus RX 350 Premium tạm t&iacute;nh ở tr&ecirc;n đ&atilde; bao gồm c&aacute;c khoản ph&iacute; bắt buộc như:</p>\r\n\r\n<ul>\r\n	<li>Lệ ph&iacute; trước bạ: H&agrave; Nội (12%), TP.HCM (10%), c&aacute;c tỉnh th&agrave;nh kh&aacute;c (10% - 12%)</li>\r\n	<li>Lệ ph&iacute; biển số: Tại H&agrave; Nội &amp; TP.HCM (20 triệu), c&aacute;c tỉnh th&agrave;nh (1 - 3 triệu)</li>\r\n	<li>Lệ ph&iacute; đăng kiểm: 50 ngh&igrave;n</li>\r\n	<li>Lệ ph&iacute; đường bộ: 130.000 ngh&igrave;n/ th&aacute;ng (c&aacute; nh&acirc;n đứng t&ecirc;n) / 180.000 ngh&igrave;n/ th&aacute;ng (c&ocirc;ng ty đứng t&ecirc;n)</li>\r\n	<li>Bảo hiểm d&acirc;n sự: 480 ngh&igrave;n.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 100, '1657436652_vn-galaxy-watch4-classic-sm-r880nzsaxxv-481128047.jpg', 3440420000, 23, 37, '2024-01-04 21:19:19');
