INSERT INTO `products` VALUES (42, 'Toyota Sienta 2022', '<h2><span style=\"font-size:16px\"><strong>T</strong></span><em>oyota Sienta 2022 tiếp tục được n&acirc;ng cấp tại thị trường Th&aacute;i Lan để gia tăng sức cạnh tranh với c&aacute;c đối thủ trong c&ugrave;ng ph&acirc;n kh&uacute;c. Đ&acirc;y l&agrave; một mẫu xe c&ograve;n kh&aacute; mới mẻ đối với người ti&ecirc;u d&ugrave;ng Việt Nam.</em></h2>\r\n', '<p>Toyota Sienta lần đầu ra mắt thị trường to&agrave;n cầu v&agrave;o năm 2003 v&agrave; được mở b&aacute;n ở một số thị trường tr&ecirc;n thế giới như Nhật Bản, Singapore, Indonesia, Malaysia, Hồng K&ocirc;ng, L&agrave;o v&agrave; Th&aacute;i Lan. Sau lần n&acirc;ng cấp v&agrave;o năm 2019, đến nay, mẫu xe n&agrave;y tiếp tục được cải tiến trang bị để cạnh tranh với c&aacute;c đối thủ.</p>\r\n\r\n<p>Trong khi đ&oacute;, Toyota vẫn chưa c&oacute; kế hoạch đưa mẫu MPV cỡ nhỏ n&agrave;y về Việt Nam.</p>\r\n', 0, 100, '1657291868_buds2tim.jpg', 6741090000, 45, 1, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (43, 'Kia Soluto', '<p>Ph&acirc;n kh&uacute;c sedan hạng B tại thị trường Việt Nam ng&agrave;y c&agrave;ng diễn ra s&ocirc;i nổi với sự tranh t&agrave;i của nhiều đối thủ kỳ cựu. Nếu những năm về trước, người ta vẫn thấy b&oacute;ng d&aacute;ng Kia Cerato chen ch&acirc;n giữa 2 ph&acirc;n kh&uacute;c B v&agrave; C th&igrave; đến thời điểm hiện tại&nbsp;<strong>Soluto sẽ đ&oacute;ng chốt v&agrave; được dự đo&aacute;n sẽ chiếm lợi thế cực lớn về gi&aacute; cả.</strong></p>\r\n', '<h3>Ngo&agrave;i ra, Vios c&ograve;n l&agrave; mẫu xe đem lại gi&aacute; trị kinh tế cao khi c&oacute; kh&ocirc;ng &iacute;t người mua xe để chạy dịch vụ. Tiết kiệm xăng l&agrave; một chuyện, Vios c&ograve;n &iacute;t hỏng vặt v&agrave; mỗi năm bạn chỉ mất tiền để thay dầu nhớt khi bảo dưỡng. Mặt kh&aacute;c, qua từng đời xe, Vios chỉ mất gi&aacute; 20 triệu/năm khi b&aacute;n lại- qu&aacute; l&yacute; tưởng để lựa chọn để sử dụng cho gia đ&igrave;nh hoặc kinh doan</h3>\r\n', 0, 100, '1657292267_vn-galaxy-buds-pro-r190-sm-r190nzkaxxv-509286836.jpg', 1768050000, 45, 5, '2023-12-18 21:19:19');
