INSERT INTO `products` VALUES (45, 'Mercedes EQV ', '<h2><strong>Mercedes-Benz EQV 2024</strong>&nbsp;l&agrave; phi&ecirc;n bản chạy điện của d&ograve;ng xe V-Class quen thuộc. Diện mạo của EQV thay đổi nhẹ để ph&ugrave; hợp với nh&atilde;n hiệu xe điện EQ.</h2>\r\n', '<p>Mercedes-Benz từng trưng b&agrave;y bản mẫu &yacute; tưởng Concept EQV tại triển l&atilde;m &ocirc; t&ocirc; Geneva 2019 hồi th&aacute;ng 03 &ndash; 2019. Cho đến th&aacute;ng 09 &ndash; 2019 xe ch&iacute;nh thức ho&agrave;n thiện để đi v&agrave;o sản xuất thương mại h&agrave;ng loạt v&agrave; được ra mắt tại triển l&atilde;m IAA Cars Frankfurt 2019.</p>\r\n\r\n<p>Từ đ&oacute; cho đến nay, c&aacute;c phi&ecirc;n bản của&nbsp;Mercedes-Benz EQV&nbsp;lu&ocirc;n d&agrave;nh được nhiều sự quan t&acirc;m của người ti&ecirc;u d&ugrave;ng.</p>\r\n', 0, 100, '1657384485_samsung-galaxy-m53-xanhduong-1.jpg', 1280770000, 20, 12, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (46, 'Hyundai Creta ', '<h2>TC Motor đưa Creta trở lại thị trường Việt để cạnh tranh ở ph&acirc;n kh&uacute;c gầm cao cỡ B. D&ograve;ng xe n&agrave;y từng xuất hiện năm 2015-2016 nhưng kh&ocirc;ng th&agrave;nh c&ocirc;ng.</h2>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p>Hyundai Creta thuộc ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/phan-khuc/phan-khuc-b-2#crossover\" rel=\"dofollow\">Crossover cỡ B</a>, mảnh đất b&eacute;o bở với sự g&oacute;p mặt nhiều sản phẩm, như&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/kia-seltos-68\" rel=\"dofollow\">Kia Seltos</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/mazda-cx-3-96\" rel=\"dofollow\">Mazda CX-3</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/mg-zs-114\" rel=\"dofollow\">MG ZS</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/volkswagen-t-cross-207\" rel=\"dofollow\">Volkswagen T-Cross</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/honda-hr-v-43\" rel=\"dofollow\">Honda HR-V</a>&nbsp;v&agrave;&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/toyota-corolla-cross-158\" rel=\"dofollow\">Toyota Corolla Cross</a>&nbsp;(thuộc B+). Đ&acirc;y l&agrave; ph&acirc;n kh&uacute;c nhắm tới đối tượng kh&aacute;ch h&agrave;ng gia đ&igrave;nh trẻ v&agrave; những người lần đầu sở hữu &ocirc;t&ocirc;.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; Creta cũng cạnh tranh một c&aacute;ch gi&aacute;n tiếp với c&aacute;c mẫu&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/phan-khuc/xe-nho-hang-ab-23#crossover\" rel=\"dofollow\">Crossover cỡ A+</a>&nbsp;như&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/kia-sonet-193\" rel=\"dofollow\">Kia Sonet</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/toyota-raize-196\" rel=\"dofollow\">Toyota Raize</a>&hellip; hoặc c&aacute;c mẫu&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/mazda-cx-30-99\" rel=\"dofollow\">Mazda CX-30</a>,&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/dong-xe/peugeot-2008-127\" rel=\"dofollow\">Peugeot 2008</a>&nbsp;thuộc&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/phan-khuc/phan-khuc-b-c-3\" rel=\"dofollow\">ph&acirc;n kh&uacute;c B+/C-</a>.</p>\r\n', 0, 100, '1657384686_samsung-galaxy-m33-xanh-1-min.jpg', 3552900000, 10, 16, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (47, 'LAND CRUISER 300', '<h2>Chiếc SUV nổi tiếng về khả năng vượt địa h&igrave;nh v&agrave; bền bỉ, tuy nhi&ecirc;n động cơ lớn, gi&aacute; b&aacute;n như xe sang v&agrave; mức ti&ecirc;u thụ nhi&ecirc;n liệu cao n&ecirc;n Land Cruiser chủ yếu l&agrave; lựa chọn của kh&aacute;ch h&agrave;ng gi&agrave;u c&oacute;,&nbsp;c&aacute;c&nbsp;tổ chức hay c&ocirc;ng ty.&nbsp;</h2>\r\n', '<p>Uy lực thống lĩnh</p>\r\n\r\n<p><a href=\"https://www.toyota.com.vn/thong-so-ky-thuat/?modelId=321&amp;gradeId=1896\" target=\"_blank\">XEM TH&Ocirc;NG SỐ</a><a href=\"https://www.toyota.com.vn/so-sanh-xe/?modelId1=321&amp;&amp;gradeId1=1896\" target=\"_blank\">SO S&Aacute;NH XE</a></p>\r\n\r\n<p>Số chỗ ngồi</p>\r\n\r\n<p>7 chỗ</p>\r\n\r\n<p>Kiểu d&aacute;ng</p>\r\n\r\n<p>Đa dụng thể thao</p>\r\n\r\n<p>Nhi&ecirc;n liệu</p>\r\n\r\n<p>Xăng</p>\r\n\r\n<p>Xuất xứ</p>\r\n\r\n<p>Nhật Bản</p>\r\n\r\n<p>Gi&aacute; từ</p>\r\n\r\n<p>4.286.000.000</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 100, '1657388480_samsung-galaxy-z-fold3-5g-xanh-reu-2.jpg', 3922190000, 10, 1, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (48, 'Mercedes EQB', '<p><strong>Mercedes-Benz EQB 2024 l&agrave; phi&ecirc;n bản n&acirc;ng cấp</strong>&nbsp;giữa v&ograve;ng đời mới nhất t&iacute;nh đến thời điểm hiện tại của mẫu xe. Xe sở hữu một số tinh chỉnh về ngoại h&igrave;nh, n&acirc;ng cấp c&ocirc;ng nghệ vượt trội, c&ugrave;ng khả năng vận h&agrave;nh mạnh mẽ.</p>\r\n', '<p>Khi m&agrave; tại thị trường trong nước chuẩn bị với sự kiện ra mắt xe&nbsp;<a href=\"https://muaxegiatot.com/xe-mercedes-eqb-250.html\" rel=\"noopener\" target=\"_blank\" title=\"Giá xe Mercedes-Benz EQB 250\">Mercedes-Benz EQB 250</a>&nbsp;v&agrave;o th&aacute;ng 9 tới đ&acirc;y.</p>\r\n\r\n<p>Th&igrave; h&atilde;ng xe nước Đức vừa qua đ&atilde; c&ocirc;ng bố về sự xuất hiện của Mercedes-Benz EQB 2024 phi&ecirc;n bản mới nhất.</p>\r\n', 0, 100, '1657385177_samsung-galaxy-tab-s7-xanh-duong-1-org.jpg', 8452260000, 15, 12, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (49, 'Honda Civic Type R', '<h2>&nbsp;</h2>\r\n\r\n<h3>BỨT TỐC VƯỢT ĐỈNH</h3>\r\n\r\n<p>D&ugrave; đ&atilde; ở đỉnh cao, chủ nh&acirc;n Honda Civic Type R vẫn kh&ocirc;ng ngừng tăng tốc để chinh phục đỉnh mới, vinh quang mới v&agrave; trở th&agrave;nh phi&ecirc;n bản th&agrave;nh c&ocirc;ng hơn của ch&iacute;nh m&igrave;nh.</p>\r\n', '<h2>GIẢI THƯỞNG TO&Agrave;N CẦU</h2>\r\n\r\n<p>Từ khi ra mắt, Honda Civic Type R tự h&agrave;o nhận được nhiều giải thưởng danh gi&aacute; tr&ecirc;n to&agrave;n cầu, như Giải thưởng &ldquo;Xe của năm&rdquo; được b&aacute;o BBC Top Gear trao tặng năm 2017; Giải thưởng &ldquo;Mẫu xe Hatchback nổi bật của năm 2020&rdquo; do tạp ch&iacute; Auto Express (Anh Quốc) trao tặng, c&ugrave;ng nhiều c&aacute;c giải thưởng uy t&iacute;n kh&aacute;c.</p>\r\n', 0, 100, '1657385351_samsung-galaxy-tab-s7-fe-wifi-1.jpg', 1494700000, 15, 14, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (50, 'Toyota Vios 2023', '<h2>Vios 2023 l&agrave; bản n&acirc;ng cấp nhẹ của mẫu sedan &quot;ăn kh&aacute;ch&quot; nhất nh&agrave; Toyota. Xe lược bỏ 3&nbsp;bản, chỉ c&ograve;n 3 bản l&agrave; E MT, E CVT (3 t&uacute;i kh&iacute;) v&agrave; G. Vios cạnh tranh với c&aacute;c đối thủ như Hyundai Accent, Honda City.&nbsp;</h2>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p>So với&nbsp;<a href=\"https://vnexpress.net/oto-xe-may/v-car/doi-xe/toyota-vios-2021-147\" rel=\"dofollow\">bản tiền nhiệm</a>, Toyota Vios 2023 lược bỏ c&aacute;c bản E MT v&agrave; E CVT 7 t&uacute;i kh&iacute;, chỉ c&ograve;n c&aacute;c bản 3 t&uacute;i kh&iacute;, gi&aacute; giảm lần lượt 10 triệu v&agrave; 14 triệu đồng. Ri&ecirc;ng bản G CVT vẫn giữ nguy&ecirc;n, kh&ocirc;ng tăng gi&aacute;. Bản G-RS vốn k&eacute;n kh&aacute;ch cũng ngưng ph&acirc;n phối.</p>\r\n\r\n<p>Về thiết kế, Toyota Vios mới được thiết kế lại phần đầu c&acirc;n đối, vu&ocirc;ng vức hơn. Đ&egrave;n pha LED trở th&agrave;nh trang bị ti&ecirc;u chuẩn, tạo h&igrave;nh khoẻ khoắn. Cản trước v&agrave; mặt ca-lăng cũng được thiết kế lại nhưng kh&aacute; rối so với bản cũ.</p>\r\n\r\n<p>So với bản đời tiền nhiệm, h&atilde;ng thay đổi thiết kế la-zăng, trong khi k&iacute;ch thước vẫn giữ 15 inch. Kh&ocirc;ng c&oacute; kh&aacute;c biệt n&agrave;o về đ&egrave;n hậu tr&ecirc;n bản mới. Ri&ecirc;ng cản sau với dải phản quang được tinh chỉnh lại.</p>\r\n\r\n<p>Ở nội thất, Vios 2023 kh&ocirc;ng thay đổi nhiều c&aacute;ch sắp xếp c&aacute;c chi tiết. H&atilde;ng n&acirc;ng cấp m&agrave;n h&igrave;nh từ 7 inch l&ecirc;n 9 inch, v&ocirc;-lăng th&ecirc;m lẫy chuyển số cho bản G. Hai bản thấp hơn th&ecirc;m cổng sạc USB cho h&agrave;ng ghế sau.</p>\r\n', 0, 100, '1657385508_samsung-galaxy-tab-s8-white-1.jpg', 616743000, 7, 1, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (51, 'HONDA BR-V', '<h2>Honda BR-V&nbsp;với &yacute; tưởng mang đến một mẫu &ldquo;Chuy&ecirc;n cơ mặt đất&rdquo; tập trung n&acirc;ng cao trải nghiệm đẳng cấp, tiện nghi của người sử dụng, Honda BR-V l&agrave; sự kết hợp ho&agrave;n hảo của một chiếc SUV c&aacute; t&iacute;nh, một chiếc MPV đa dụng c&ugrave;ng với cảm gi&aacute;c l&aacute;i hứng khởi lu&ocirc;n c&oacute; tr&ecirc;n c&aacute;c d&ograve;ng xe Honda.</h2>\r\n', '<h2>TẬN HƯỞNG TRẢI NGHIỆM VUI VẺ B&Ecirc;N NGƯỜI TH&Acirc;N</h2>\r\n\r\n<p>Với nội thất rộng r&atilde;i, tiện nghi c&ugrave;ng khả năng vận h&agrave;nh &ecirc;m &aacute;i, Honda BR-V mang đến cho cả gia đ&igrave;nh những gi&acirc;y ph&uacute;t thoải m&aacute;i, an to&agrave;n tr&ecirc;n mọi cung đường như một chiếc chuy&ecirc;n cơ đ&iacute;ch thực<br />\r\n<img alt=\"\" src=\"https://otohonda-haiphong.vn/wp-content/uploads/2023/07/honda-br-v-080723-02.jpg\" style=\"height:675px; width:1200px\" />Với mong muốn hướng đến kh&aacute;ch h&agrave;ng l&agrave; c&aacute;c gia đ&igrave;nh Việt, Honda BR-V mang đến những ưu việt trong d&ograve;ng xe gia đ&igrave;nh qua c&aacute;c kh&iacute;a cạnh:</p>\r\n\r\n<ul>\r\n	<li>Thiết kế nội thất rộng r&atilde;i 7 chỗ ngồi c&ugrave;ng c&aacute;c h&agrave;ng ghế linh hoạt ph&ugrave; hợp với c&aacute;c gia đ&igrave;nh c&oacute; nhiều thế hệ.</li>\r\n	<li>Khả năng vận h&agrave;nh v&agrave; tiết kiệm nhi&ecirc;n liệu vượt trội.</li>\r\n	<li>C&aacute;c t&iacute;nh năng an to&agrave;n chủ động v&agrave; bị động ti&ecirc;n tiến, đặc biệt l&agrave; hệ thống Honda SENSING gi&uacute;p cả gia đ&igrave;nh lu&ocirc;n an t&acirc;m v&agrave; tận hưởng trọn vẹn niềm vui trong mỗi chuyến đi.</li>\r\n	<li>Hệ thống trang bị hỗ trợ hiện đại tr&ecirc;n xe mang đến trải nghiệm đẳng cấp c&ugrave;ng sự tiện nghi, thoải m&aacute;i tối đa cho tất cả c&aacute;c th&agrave;nh vi&ecirc;n.</li>\r\n</ul>\r\n', 0, 100, '1657385777_samsung-galaxy-tab-a7-lite-1-3-org.jpg', 7599610000, 11, 14, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (52, 'Mazda CX-5', '<h2>Mazda CX-5 l&agrave; c&aacute;i t&ecirc;n &ldquo;độc chiếm&rdquo; vị tr&iacute; số 1 về doanh số trong th&aacute;ng 10/2023 với hơn 2.000 xe b&aacute;n ra, bỏ xa xe ở ph&iacute;a sau với c&aacute;ch biệt hơn 700 xe.</h2>\r\n', '<h3>Kết th&uacute;c th&aacute;ng 11/2023, thị trường &ocirc; t&ocirc; Việt Nam vẫn chưa cho thấy sự khởi sắc d&ugrave; đ&acirc;y đ&atilde; l&agrave; thời điểm mua sắm cuối năm. Trong Top 10 xe b&aacute;n chạy nhất th&aacute;ng 11/2023, ngo&agrave;i những c&aacute;i t&ecirc;n quen thuộc như Mazda CX-5, Hyundai Accent th&igrave; c&oacute; th&ecirc;m loạt xe Toyota nổi bật l&agrave; Yaris Cross, Veloz Cross hay Vios.</h3>\r\n', 0, 100, '1657385959_samsung-galaxy-tab-a8-1-1.jpg', 7147830000, 12, 1, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (53, 'Ford Ranger Raptor 2022', '<h2>Ford Ranger Raptor 2023 ra mắt bản n&acirc;ng cấp, c&oacute; thay đổi g&acirc;y tranh c&atilde;i</h2>\r\n', '<p>X&eacute;t về mặt kinh tế, việc Ranger Raptor chuyển sang đăng k&yacute; dạng b&aacute;n tải chở h&agrave;ng th&ocirc;ng thường sẽ&nbsp;<strong>gi&uacute;p kh&aacute;ch h&agrave;ng tiết kiệm gần 60 triệu đồng.</strong></p>\r\n\r\n<p>Cụ thể, lệ ph&iacute; trước bạ mới chỉ bằng 60% mức cũ. Tại H&agrave; Nội, Ranger Raptor sẽ c&oacute; ph&iacute; trước bạ mới 7,2%, giảm 4,8% so với trước. C&aacute;c tỉnh/th&agrave;nh kh&aacute;c c&oacute; ph&iacute; trước bạ mới 6%, giảm 4% so với trước.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 0, 100, '1657418987_vn-crystal-uhd-au8000-ua65au8000kxxv-408312393.jpg', 2940300000, 10, 8, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (54, 'Toyota Corolla Altis', '<h2>Toyota Corolla Altis ch&iacute;nh l&agrave; c&aacute;i t&ecirc;n nổi tiếng một thời khuynh đảo ph&acirc;n kh&uacute;c Sedan hạng C với doanh số cao vượt trội. Tuy nhi&ecirc;n trong những năm trở lại đ&acirc;y, xe đang c&oacute; dấu hiệu đuối sức v&agrave; gặp nhiều kh&oacute; khăn khi cạnh tranh với c&aacute;c đối thủ.&nbsp;</h2>\r\n', '<p>Để cải thiện t&igrave;nh trạng tr&ecirc;n, mới đ&acirc;y&nbsp;<strong>Toyota đ&atilde; giới thiệu phi&ecirc;n bản mới nhất của Corolla Altis c&ugrave;ng nhiều thay đổi</strong>, qua đ&oacute; gi&uacute;p xe trở n&ecirc;n hấp dẫn hơn. Năm 2019, sản phẩm n&agrave;y đ&atilde; được giới thiệu tại một số thị trường v&agrave; cũng được đồn đo&aacute;n l&agrave; sẽ về Việt Nam ngay trong năm nay. Hiện tại một số Showroom đ&atilde; bắt đầu nhận đặt cọc sớm.&nbsp;</p>\r\n', 0, 100, '1657419493_vn-qled-q80b-qa65q80bakxxv-531464575.jpg', 2258010000, 12, 1, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (55, 'HONDA CITY', '<p>Honda City&nbsp;kh&ocirc;ng ngừng chuyển m&igrave;nh trong đường n&eacute;t thiết kế, đột ph&aacute; với c&ocirc;ng nghệ ti&ecirc;n tiến để c&ugrave;ng bạn vươn xa đầy ki&ecirc;u h&atilde;nh tr&ecirc;n h&agrave;nh tr&igrave;nh kiến tạo những th&agrave;nh c&ocirc;ng mới.</p>\r\n', '<p>Honda City mới tập trung v&agrave;o 4 gi&aacute; trị nổi bật:&nbsp;<strong>&ldquo;Thể thao c&aacute; t&iacute;nh &ndash; Tiện nghi hiện đại &ndash; An to&agrave;n vượt trội &ndash; Vận h&agrave;nh mạnh mẽ&rdquo;</strong>, hướng tới những kh&aacute;ch h&agrave;ng trẻ trung, ưa th&iacute;ch sự năng động, hiện đại c&ugrave;ng sự tiện nghi v&agrave; an to&agrave;n tối đa.</p>\r\n\r\n<p><img alt=\"\" src=\"https://otohonda-haiphong.vn/wp-content/uploads/2023/07/honda-city-0723-02.jpg\" style=\"height:675px; width:1200px\" /></p>\r\n\r\n<p>Được ph&aacute;t triển dựa tr&ecirc;n &yacute; tưởng mang đến một mẫu xe sedan &ldquo;Thể thao hơn, An to&agrave;n hơn&rdquo;, Honda City mới kh&ocirc;ng chỉ đ&aacute;p ứng nhu cầu đi lại cơ bản m&agrave; c&ograve;n đem lại sự an t&acirc;m khi trải nghiệm cho kh&aacute;ch h&agrave;ng với những thay đổi đ&aacute;ng gi&aacute; trong ph&acirc;n kh&uacute;c hạng B:</p>\r\n\r\n<ul>\r\n	<li>Thiết kế thể thao, c&aacute; t&iacute;nh hơn với lưới tản nhiệt tổ ong, cản trước v&agrave; cản sau được tạo h&igrave;nh khuếch t&aacute;n l&agrave;m tăng độ khoẻ khoắn, la zăng mới với thiết kế phong c&aacute;ch hơn, nội thất bọc da sang trọng (RS, L).</li>\r\n</ul>\r\n', 0, 100, '1657432571_vn-the-sero-ls05b-qa43ls05bakxxv-531464633.jpg', 1969150000, 0, 14, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (56, ' Hyundai Ioniq 5', '<h2>L&agrave; mẫu xe k&iacute;n tiếng nhất, nhưng Hyundai Ioniq 5 bất ngờ mở b&aacute;n sớm nhất trong năm nay khi sẽ ra mắt v&agrave;o ng&agrave;y 26 th&aacute;ng 4 năm 2022.</h2>\r\n', '<p>Về tổng thể, Hyundai Ioniq 5 sở hữu k&iacute;ch thước D&agrave;i x Rộng x Cao lần lượt l&agrave; 4.635 x 1.890 x 1.605 (mm), chiều d&agrave;i cơ sở 3.000mm. K&iacute;ch thước của chiếc xe điện n&agrave;y c&ograve;n lớn hơn cả những chiếc SUV cỡ lớn như&nbsp;<a href=\"https://xetot.com/toan-quoc/mua-ban-oto/hyundai--palisade\" rel=\"noopener\" target=\"_blank\">Hyundai Palisade</a>.</p>\r\n\r\n<p>Ở ngoại h&igrave;nh, Hyundai Ioniq 5 g&acirc;y ấn tượng với người d&ugrave;ng bởi thiết kế trẻ trung, hiện đại.</p>\r\n', 1, 94, '1657437871_vn-side-by-side-rs62r5001b4-rs62r5001b4-sv-black-198134679.jpg', 2571740000, 18, 31, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (57, 'Ford Everest Sport ', '<h2>Ford Everest Sport 2.0L 4&times;2 AT 2023 l&agrave; một chiếc xe SUV tuyệt vời với nhiều t&iacute;nh năng hấp dẫn v&agrave; sức mạnh đ&aacute;ng kinh ngạc. Xe sử dụng động cơ Single Turbo Diesel 2.0L i4 TDCi, với c&ocirc;ng suất 170 m&atilde; lực (125 KW) v&agrave; m&ocirc;-men xoắn cực đại 405 Nm/ 1750-2500 rpm. Sức mạnh n&agrave;y được truyền đến hộp số tự động 6 cấp, với phanh tay điện tử gi&uacute;p cho việc điều khiển xe trở n&ecirc;n dễ d&agrave;ng hơn.</h2>\r\n', '<table border=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>T&igrave;nh Trạng Xe</strong></td>\r\n			<td>Mới 100%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Năm Sản xuất</strong></td>\r\n			<td>2023</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Kiểu Xe</strong></td>\r\n			<td>SUV 7 Chỗ</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Động Cơ</strong></td>\r\n			<td>Dầu Diesel 2.0L Single-Turbo</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hộp Số</strong></td>\r\n			<td>Tự Động 6 Cấp</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ Thống Đẫn Động</strong></td>\r\n			<td>Một Cầu 4&times;2</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>K&iacute;ch Thước DxRxC</strong></td>\r\n			<td>4914 x 1923 x 1842</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>C&ocirc;ng xuất cực Đại</strong></td>\r\n			<td>170 (125Kw ) 3500</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>M&agrave;u Xe</strong></td>\r\n			<td>Bạc, X&aacute;m Meteor, Xanh Dương, N&acirc;u, Đen, Trắng Tuyết, Đỏ Cam</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/Ford-Everest-Sport-2.0L-4%C3%972-AT.jpg\"><img alt=\"Ford Everest Sport 2.0L 4×2 AT\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/Ford-Everest-Sport-2.0L-4%C3%972-AT-600x400.jpg\" style=\"height:400px; width:600px\" title=\"Ford Everest Sport 2.0L 4×2 AT\" /></a></p>\r\n\r\n<p><a href=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/ford-evrest-sport-4x2-at-12.jpg\"><img alt=\"\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/ford-evrest-sport-4x2-at-12-600x495.jpg\" style=\"height:495px; width:600px\" title=\"ford-evrest-sport-4x2-at-12\" /></a></p>\r\n\r\n<p><a href=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/noi-that-xe-ford-everest-sport-2023-moi-nhat-798x446-1.jpg\"><img alt=\"\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/noi-that-xe-ford-everest-sport-2023-moi-nhat-798x446-1-600x335.jpg\" style=\"height:335px; width:600px\" title=\"noi-that-xe-ford-everest-sport-2023-moi-nhat-798x446-1\" /></a></p>\r\n\r\n<p><a href=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/ket-noi-khong-day-tren-ford-everest-moi-1.jpg\"><img alt=\"\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/ket-noi-khong-day-tren-ford-everest-moi-1-600x326.jpg\" style=\"height:326px; width:600px\" title=\"ket-noi-khong-day-tren-ford-everest-moi-1\" /></a></p>\r\n\r\n<p><img alt=\"Ford Everest Sport 2.0L 4×2 AT\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/Ford-Everest-Sport-2.0L-4%C3%972-AT-300x200.jpg\" style=\"width:300px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/ford-evrest-sport-4x2-at-12-300x248.jpg\" style=\"width:300px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/noi-that-xe-ford-everest-sport-2023-moi-nhat-798x446-1-300x168.jpg\" style=\"width:300px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://ford-giaiphong.net/wp-content/uploads/2023/04/ket-noi-khong-day-tren-ford-everest-moi-1-300x163.jpg\" style=\"width:300px\" /></p>\r\n', 0, 100, '1657438407_vn-rt6000k-top-freezer-with-twin-cooling-plus™-530l-417759-rt29k503jb1-sv-532234455.jpg', 6451220000, 18, 8, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (58, 'Lynk & Co 09', '<h2>Lynk &amp; Co 09 l&agrave; mẫu xe cao cấp nhất đến thời điểm hiện tại của h&atilde;ng xe Trung Quốc hiện mới c&oacute; mặt tại Việt Nam. Mẫu xe được định vị tại ph&acirc;n kh&uacute;c SUV hạng E với thiết kế ấn tượng, c&ugrave;ng mức gi&aacute; kh&aacute; cao 2,199 tỷ.</h2>\r\n', '<p><a href=\"https://xetot.com/tin-xe/gia-xe-o-to-lynk-co/\" rel=\"noopener\" target=\"_blank\" title=\"Giá xe Lynk &amp; Co\">H&atilde;ng xe Trung Quốc Lynk &amp; Co</a>&nbsp;đ&atilde; ch&iacute;nh thức ra mắt thị trường Việt Nam v&agrave;o ng&agrave;y 16 &ndash; 12 &ndash; 2023. Cũng trong khu&ocirc;n khổ sự kiện n&agrave;y, Lynk &amp; Co 09 l&agrave; một trong 3 mẫu xe của thương hiệu được giới thiệu đến người ti&ecirc;u d&ugrave;ng trong nước.</p>\r\n\r\n<p><strong>Lynk &amp; Co 09</strong>&nbsp;l&agrave; mẫu xe cao cấp nhất t&iacute;nh đến thời điểm hiện tại của Lynk &amp; Co ra mắt người d&ugrave;ng Việt. Mẫu xe được định vị tại&nbsp;<strong>ph&acirc;n kh&uacute;c SUV hạng E với 7 chỗ ngồi</strong>.</p>\r\n\r\n<p>Xe sở hữu thiết kế kh&aacute; ấn tượng, nhiều trang bị tiện nghi hiện đại v&agrave; c&ocirc;ng nghệ an to&agrave;n cao cấp l&agrave; những điểm s&aacute;ng nổi trội của mẫu xe trong lần xuất hiện ch&iacute;nh thức n&agrave;y.</p>\r\n', 0, 100, '1657438578_vn-ref-rb4000-387691-rb30n4190bu-sv-518038749.jpg', 5040910000, 15, 31, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (59, 'VinFast Fadil', '<h2>Mẫu xe cỡ A, được ph&aacute;t triển tr&ecirc;n nền tảng mẫu Opel Karl Rock với m&aacute;y 1.4, hộp số CVT v&agrave; nhiều trang bị an to&agrave;n. Với gi&aacute; b&aacute;n v&agrave; trang bị hợp l&yacute; Fadil nhanh ch&oacute;ng &aacute;p đảo trong ph&acirc;n kh&uacute;c xe cỡ A.</h2>\r\n', '<h2><strong>M&ocirc; tả / đ&aacute;nh gi&aacute; chi tiết</strong></h2>\r\n\r\n<p><strong>Một số th&ocirc;ng số kỹ thuật về động cơ, hệ thống treo, phanh:</strong></p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>Th&ocirc;ng số kỹ thuật</strong></td>\r\n			<td><strong>Ti&ecirc;u chuẩn</strong></td>\r\n			<td><strong>N&acirc;ng cao</strong></td>\r\n			<td><strong>Cao cấp</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Động cơ</td>\r\n			<td colspan=\"3\">1.4l, Xăng, 4 xi lanh thẳng h&agrave;ng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&ocirc;ng suất</td>\r\n			<td colspan=\"3\">98 m&atilde; lực tại 6200 v&ograve;ng/ph&uacute;t</td>\r\n		</tr>\r\n		<tr>\r\n			<td>M&ocirc;-men xoắn</td>\r\n			<td colspan=\"3\">128Nm tại 4400 v&ograve;ng/ph&uacute;t</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hộp số</td>\r\n			<td colspan=\"3\">Tự động v&ocirc; cấp - CVT</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hệ dẫn động</td>\r\n			<td colspan=\"3\">Cầu trước - FWD</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Mức ti&ecirc;u thụ nhi&ecirc;n liệu</td>\r\n			<td colspan=\"3\">5.85l/100 km tr&ecirc;n đường hỗn hợp</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Lốp, la-zăng</td>\r\n			<td>M&agrave; đ&uacute;c hợp kim nh&ocirc;m 15&quot;</td>\r\n			<td colspan=\"2\">M&agrave;n đ&uacute;c hợp kim nh&ocirc;m 2 m&agrave;u đen/x&aacute;m 15&quot;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Treo trước</td>\r\n			<td colspan=\"3\">Kiểu MacPherson</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Treo sau</td>\r\n			<td colspan=\"3\">Phụ thuộc, kiểu dầm xoắn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Phanh trước</td>\r\n			<td colspan=\"3\">Đĩa</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Phanh sau</td>\r\n			<td colspan=\"3\">Tang trống</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trợ lực v&ocirc;-lăng</td>\r\n			<td colspan=\"3\">Trợ lực điện</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 0, 100, '1657438767_vn-bottom-mount-freezer-rb30n4010s8-rb30n4010s8-sv-frontsilver-115806204.jpg', 5350890000, 22, 10, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (60, 'KIA Seltos', '<h2>KIA&nbsp;Seltos l&agrave; một mẫu SUV hạng B gi&aacute; rẻ của h&atilde;ng KIA (H&agrave;n Quốc) cạnh tranh trực tiếp với Toyota Corolla Cross. Seltos c&oacute; gi&aacute; b&aacute;n dễ tiếp cận hơn từ 629 &ndash; 717 triệu đồng. Trong th&aacute;ng 12-2021, mẫu xe H&agrave;n sẽ c&oacute; lợi thế khi được giảm 50% ph&iacute; trước bạ.</h2>\r\n', '<p>Hiện tại, KIA Seltos được lắp r&aacute;p trong nước với 4 phi&ecirc;n bản cho 2 t&ugrave;y chọn động cơ. Bản động cơ Turbo 1.4L c&ocirc;ng suất 138 m&atilde; lực, momen xoắn 242 Nm, kết hợp hộp số tự động ly hợp k&eacute;p 7 cấp. Bản động cơ 1.6L c&ocirc;ng suất 128 m&atilde; lực, momen xoắn 157 Nm, kết hợp hộp số tự động 6 cấp.</p>\r\n\r\n<p>Trong ph&acirc;n kh&uacute;c SUV gi&aacute; rẻ, KIA Seltos được kh&aacute;ch h&agrave;ng đ&aacute;nh gi&aacute; cao với mức gi&aacute; b&aacute;n hấp dẫn, ngoại thất trẻ trung, thiết kế nội thất bắt mắt, rộng r&atilde;i, được trang bị nhiều tiện nghi hiện đại như: M&agrave;n h&igrave;nh cảm ứng trung t&acirc;m 10.25 inch, cửa sổ trời, đ&egrave;n nội thất 8 m&agrave;u,&hellip; Gi&aacute; b&aacute;n của mẫu SUV n&agrave;y với 4 phi&ecirc;n bản như sau:</p>\r\n', 0, 100, '1657441599_vn-microwave-oven-grill-mg30t5018ck-mg30t5018ck-sv-frontblack-224903476.jpg', 1631700000, 36, 5, '2024-01-04 21:19:19');
INSERT INTO `products` VALUES (61, 'Toyota Corolla Cross', '<h2><span style=\"font-size:16px\"><strong>T</strong></span>oyota Corolla Cross l&agrave; một mẫu SUV hạng B gi&aacute; rẻ của Toyota, được nhập khẩu từ Th&aacute;i Lan với 3 phi&ecirc;n bản: 1.8G, 1.8V v&agrave; 1.8HV. Corolla Cross c&oacute; 2 t&ugrave;y chọn động cơ cho 3 phi&ecirc;n bản n&agrave;y đ&oacute; l&agrave;: Động cơ xăng 1.8L 138 m&atilde; lực, m&ocirc; men xoắn 172 Nm v&agrave; động cơ Hybrid gồm động cơ xăng 1.8L kết hợp với m&ocirc; tơ điện cho tổng c&ocirc;ng suất 150 m&atilde; lực, m&ocirc; men xoắn 305 Nm. C&ugrave;ng với 2 động cơ n&agrave;y l&agrave; hộp số tự động v&ocirc; cấp CVT.</h2>\r\n', '<h3>T&iacute;nh trong th&aacute;ng 11-2021, doanh số b&aacute;n ra của Toyota Corolla Cross đạt 3346 xe, tăng gần 1000 xe so với th&aacute;ng 10-2021. Đặc biệt l&agrave; ẵm ng&ocirc;i vua doanh số b&aacute;n chạy nhất thị trường trong th&aacute;ng 11-2021. Đặc biệt, Toyota Corolla Cross c&ograve;n cho thấy sự bứt ph&aacute; ngoạn mục về doanh số trong thời gian gần đ&acirc;y, vượt qua cả KIA Seltos về doanh số b&aacute;n cộng dồn từ đầu năm.</h3>\r\n', 0, 100, '1657442315_vn-canister-vcc8836v36-vcc8836v36-xsv-frontblue-216144557.jpg', 1105840000, 7, 1, '2023-12-18 21:19:19');
INSERT INTO `products` VALUES (62, 'Haima 7X', '<h2>Haima l&agrave; thương hiệu &ocirc; t&ocirc; Trung Quốc từng x&acirc;m nhập thị trường Việt Nam c&aacute;ch đ&acirc;y hơn 10 năm trong cuộc độ bộ cũng nhiều &ldquo;đồng hương&rdquo; kh&aacute;c như MG, Chery. Tuy nhi&ecirc;n, chung số phận, Haima v&agrave; c&aacute;c h&atilde;ng xe Trung Quốc đều nhận thất bại ở Việt Nam do vướng phải định kiến.</h2>\r\n', '<p>Đến giờ, loạt thương hiệu n&agrave;y quyết định một lần nữa quay trở lại để thuyết phục người d&ugrave;ng Việt. Trong đ&oacute;,&nbsp;<strong>Haima 7X &ldquo;nổ ph&aacute;t s&uacute;ng&rdquo; đầu ti&ecirc;n của Haima với tham vọng đ&aacute;nh chiếm ph&acirc;n kh&uacute;c MPV 7 chỗ cỡ nhỏ, gi&aacute; rẻ.</strong></p>\r\n\r\n<p>Con đường ph&iacute;a trước của Haima 7X 2024 l&agrave; rất kh&oacute; khăn khi c&aacute;c đối thủ sừng sỏ c&ograve;n chờ như Mitsubishi Xpander,&nbsp;<a href=\"https://xetot.com/tin-xe/danh-gia-xe-toyota-veloz/\" title=\"Giá xe Toyota Veloz Cross 2024\">Toyota Veloz Cross</a>, Avanza Premio, Suzuki XL7, Ertiga.</p>\r\n', 0, 100, '1657444215_z3556173778725_54874919c0fa71041f7b233ae6ae4b71.jpg', 9634100000, 21, 2, '2024-01-04 21:19:19');
